// src/services/workerApi.js
import axios from 'axios';

// Assuming your backend is running on http://localhost:8082
const API_BASE_URL = 'http://localhost:8082/api/worker';

export const registerWorker = async (workerData) => {
    try {
        const response = await axios.post(`${API_BASE_URL}/register`, workerData);
        return response.data; // Returns "Worker registered successfully!" on success
    } catch (error) {
        throw error.response ? error.response.data : error.message;
    }
};

export const loginWorker = async (credentials) => {
    try {
        const response = await axios.post(`${API_BASE_URL}/login`, credentials);
        return response.data; // Returns { message, userId, token, role } on success
    } catch (error) {
        throw error.response ? error.response.data : error.message;
    }
};

// You might also want a logout function, though it's typically handled client-side
// by clearing the token.
