import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css'; // Don't forget to import the CSS

function Pickup() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    date: null, // Change to null initially for DatePicker
    time: '',
    address: '',
    pincode: '',
    city: '',
    state: '',
    schedulerName: '',
    phone: '',
    email: '',
    image: null,
    wasteType: '',
  });

  const [imagePreview, setImagePreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [pincodeError, setPincodeError] = useState('');
  const [dailyPickupCounts, setDailyPickupCounts] = useState({}); // State to store counts
  const MAX_PICKUPS_PER_DAY = 5; // Define your limit here
  const fileInputRef = useRef(null);

  const userId = localStorage.getItem('userId');
  const userRole = localStorage.getItem('userRole');

  useEffect(() => {
    const isLoggedIn = localStorage.getItem('isLoggedIn');
    if (!isLoggedIn || !userId) navigate('/login');
  }, [navigate, userId]);

  // Effect to watch for pincode changes and fetch location
  useEffect(() => {
    const fetchLocation = async () => {
      const { pincode } = formData;
      if (pincode.length === 6) {
        setPincodeError('');
        try {
          const response = await fetch(`https://api.postalpincode.in/pincode/${pincode}`);
          const data = await response.json();

          if (data && data[0] && data[0].Status === 'Success') {
            const postOffice = data[0].PostOffice[0];
            setFormData((prev) => ({
              ...prev,
              city: postOffice.District,
              state: postOffice.State,
            }));
          } else {
            setPincodeError('Invalid Pincode. Please enter a valid 6-digit Pincode.');
            setFormData((prev) => ({
              ...prev,
              city: '',
              state: '',
            }));
          }
        } catch (error) {
          console.error('Error fetching pincode data:', error);
          setPincodeError('Failed to fetch location. Please enter manually.');
          setFormData((prev) => ({
            ...prev,
            city: '',
            state: '',
          }));
        }
      } else if (pincode.length > 0 && pincode.length < 6) {
        setPincodeError('Pincode must be 6 digits.');
        setFormData((prev) => ({
          ...prev,
          city: '',
          state: '',
        }));
      } else {
        setPincodeError('');
        setFormData((prev) => ({
          ...prev,
          city: '',
          state: '',
        }));
      }
    };

    const debounceTimeout = setTimeout(() => {
      fetchLocation();
    }, 500);

    return () => clearTimeout(debounceTimeout);
  }, [formData.pincode]);

  // NEW useEffect to fetch daily pickup counts from your backend
  useEffect(() => {
    const fetchPickupCounts = async () => {
      const today = new Date();
      // Fetch counts for current month + next 2 months for reasonable coverage
      const startDate = today.toISOString().split('T')[0];
      const futureDate = new Date(today.getFullYear(), today.getMonth() + 2, 0); // End of 2 months from now
      const endDate = futureDate.toISOString().split('T')[0];

      try {
        const response = await fetch(`http://localhost:8082/api/pickups/counts?startDate=${startDate}&endDate=${endDate}`);
        if (!response.ok) {
          throw new Error('Failed to fetch pickup counts');
        }
        const data = await response.json();
        setDailyPickupCounts(data); // Backend returns string dates, which is fine
      } catch (error) {
        console.error('Error fetching daily pickup counts:', error);
        // Optionally, show a user-friendly error message
      }
    };

    fetchPickupCounts();
  }, []); // Run once on component mount to get initial counts

  const getTodayDate = () => {
    const today = new Date();
    today.setHours(0, 0, 0, 0); // Reset time for comparison
    return today;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleDateChange = (date) => {
    // DatePicker returns a Date object, convert to YYYY-MM-DD string for form data
    setFormData((prev) => ({ ...prev, date: date ? date.toISOString().split('T')[0] : null }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData((prev) => ({ ...prev, image: file }));
      setImagePreview(URL.createObjectURL(file));
    }
  };

  const submitPickupDetails = async () => {
    try {
      setLoading(true);
      const formDataToSend = new FormData();
      formDataToSend.append('userId', userId);
      formDataToSend.append('date', formData.date); // This is already YYYY-MM-DD string
      formDataToSend.append('time', formData.time);
      formDataToSend.append('address', formData.address);
      formDataToSend.append('pincode', formData.pincode);
      formDataToSend.append('city', formData.city);
      formDataToSend.append('state', formData.state);
      formDataToSend.append('schedulerName', formData.schedulerName);
      formDataToSend.append('phone', formData.phone);
      formDataToSend.append('email', formData.email);
      formDataToSend.append('wasteType', formData.wasteType);
      formDataToSend.append('status', 'Pending');
      if (formData.image) {
        formDataToSend.append('image', formData.image);
      }

      // **IMPORTANT**: Change this endpoint to your new /api/pickups/schedule
      const res = await fetch('http://localhost:8082/api/pickups/schedule', {
        method: 'POST',
        body: formDataToSend,
      });

      if (res.ok) {
        alert('Pickup scheduled successfully!');
        navigate('/profile');
        // OPTIONAL: Optimistically update dailyPickupCounts for the selected date
        // This avoids another API call to refetch all counts immediately
        const newCount = (dailyPickupCounts[formData.date] || 0) + 1;
        setDailyPickupCounts(prev => ({ ...prev, [formData.date]: newCount }));
      } else {
        const errorText = await res.text();
        alert(`Failed to schedule pickup. Please try again. Error: ${errorText}`);
      }
    } catch (err) {
      console.error('Error scheduling pickup:', err);
      alert('Error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handlePayment = async () => {
    // Basic validation before payment
    if (!formData.date || !formData.time || !formData.address || !formData.pincode ||
        !formData.city || !formData.state || !formData.schedulerName || !formData.phone ||
        !formData.email || !formData.wasteType || !formData.image) {
        alert("Please fill in all required fields and upload an image before proceeding to payment.");
        return;
    }
    if (pincodeError) {
        alert(pincodeError);
        return;
    }

    // Check if the selected date is full before initiating payment
    if (formData.date) {
        const selectedDateCount = dailyPickupCounts[formData.date] || 0;
        if (selectedDateCount >= MAX_PICKUPS_PER_DAY) {
            alert(`Sorry, the selected date (${formData.date}) has reached its maximum number of pickups (${MAX_PICKUPS_PER_DAY}). Please choose another date.`);
            return;
        }
    }


    try {
      const orderRes = await fetch('http://localhost:8082/api/razorpay/create-order', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ amount: 4000 }) // ‚Çπ40
      });

      if (!orderRes.ok) {
        const errorData = await orderRes.json();
        throw new Error(errorData.error || 'Failed to create Razorpay order');
      }

      const { id: order_id, currency, amount } = await orderRes.json();

      const options = {
        key: 'rzp_test_bx3dOuw8U5uwJ3', // Make sure this is your actual test key
        amount,
        currency,
        name: 'E-Waste Management',
        description: 'Pickup Charge',
        order_id,
        handler: async (response) => {
          // You might want to send response.razorpay_payment_id, etc., to backend for verification
          await submitPickupDetails(); // after payment, submit details
        },
        prefill: {
          name: formData.schedulerName,
          email: formData.email,
          contact: formData.phone
        },
        theme: { color: '#2f855a' }
      };

      const razor = new window.Razorpay(options);
      razor.open();
    } catch (err) {
      console.error('Payment Error:', err);
      alert('Payment initiation failed: ' + err.message);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.image) {
      alert("Please upload an image of the item.");
      return;
    }
    if (pincodeError) {
        alert(pincodeError);
        return;
    }

    // Double-check date availability on submit as well
    if (formData.date) {
        const selectedDateCount = dailyPickupCounts[formData.date] || 0;
        if (selectedDateCount >= MAX_PICKUPS_PER_DAY) {
            alert(`Sorry, the selected date (${formData.date}) has reached its maximum number of pickups (${MAX_PICKUPS_PER_DAY}). Please choose another date.`);
            return;
        }
    }


    if (userRole === 'charity') {
      submitPickupDetails();
    } else {
      handlePayment();
    }
  };

  // Function to determine if a date should be disabled in DatePicker
  const isDateDisabled = (date) => {
    const dateString = date.toISOString().split('T')[0];
    const count = dailyPickupCounts[dateString] || 0;
    return count >= MAX_PICKUPS_PER_DAY;
  };

  // Function to apply custom class for full dates in DatePicker
  const highlightDates = (date) => {
    const dateString = date.toISOString().split('T')[0];
    const count = dailyPickupCounts[dateString] || 0;
    return count >= MAX_PICKUPS_PER_DAY ? 'red-date' : '';
  };


  return (
    <div className="flex bg-gray-50 min-h-screen">
      {/* Main content */}
      <main className="flex-1 p-10">
        <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow">
          <button onClick={() => navigate('/profile')} className="mb-4 bg-gray-300 hover:bg-gray-400 px-4 py-2 rounded">
            ‚Üê Back to Dashboard
          </button>

          <h2 className="text-2xl font-bold mb-6 text-green-800">Schedule E-Waste Pickup</h2>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="flex space-x-4">
              {/* DatePicker Component */}
              <div className="w-1/2"> {/* Wrap DatePicker in a div for layout */}
                <DatePicker
                  selected={formData.date ? new Date(formData.date) : null} // Convert string date back to Date object
                  onChange={handleDateChange}
                  minDate={getTodayDate()} // Disable past dates
                  dateFormat="yyyy-MM-dd"
                  placeholderText="Select Date"
                  className="w-full p-2 border rounded" // Apply styling here
                  wrapperClassName="w-full" // Ensure wrapper takes full width of its parent (w-1/2 div)
                  required
                  filterDate={(date) => {
                    const today = getTodayDate(); // Get today's date with time reset
                    if (date < today) { // Disable past dates
                        return false;
                    }
                    return !isDateDisabled(date); // Disable if count exceeds limit
                  }}
                  dayClassName={highlightDates} // Apply custom class for highlighting
                />
              </div>
              <input type="time" name="time" value={formData.time} onChange={handleChange} required className="w-1/2 p-2 border rounded" />
            </div>
            <input type="text" name="address" value={formData.address} onChange={handleChange} placeholder="Full Address" required className="w-full p-2 border rounded" />
            <div className="flex space-x-4">
              <input
                type="text"
                name="pincode"
                value={formData.pincode}
                onChange={handleChange}
                placeholder="Pincode"
                required
                className={`w-1/3 p-2 border rounded ${pincodeError ? 'border-red-500' : ''}`}
                maxLength="6"
              />
              <input type="text" name="city" value={formData.city} onChange={handleChange} placeholder="City" required className="w-1/3 p-2 border rounded" readOnly={formData.city !== ''} />
              <input type="text" name="state" value={formData.state} onChange={handleChange} placeholder="State" required className="w-1/3 p-2 border rounded" readOnly={formData.state !== ''} />
            </div>
            {pincodeError && <p className="text-red-500 text-sm">{pincodeError}</p>}

            <div className="flex space-x-4">
              <input type="text" name="schedulerName" value={formData.schedulerName} onChange={handleChange} placeholder="Your Name" required className="w-1/2 p-2 border rounded" />
              <input type="tel" name="phone" value={formData.phone} onChange={handleChange} placeholder="Phone Number" required className="w-1/2 p-2 border rounded" />
            </div>
            <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Email" required className="w-full p-2 border rounded" />

            {/* Waste Type Dropdown */}
            <div className="flex flex-col">
              <label className="text-gray-700 mb-2">Select E-Waste Type</label>
              <select
                name="wasteType"
                value={formData.wasteType}
                onChange={handleChange}
                required
                className="p-2 border rounded"
              >
                <option value="">Select Waste Type</option>
                <option value="COMPUTERS">Computers</option>
                <option value="LAPTOPS">Laptops</option>
                <option value="MONITORS">Monitors</option>
                <option value="TELEVISIONS">Televisions</option>
                <option value="MOBILE_PHONES">Mobile Phones</option>
                <option value="TABLETS">Tablets</option>
                <option value="BATTERIES">Batteries</option>
                <option value="PRINTERS">Printers</option>
                <option value="FAX_MACHINES">Fax Machines</option>
                <option value="SCANNERS">Scanners</option>
                <option value="KEYBOARDS">Keyboards</option>
                <option value="MICE">Mice</option>
                <option value="CHARGERS">Chargers</option>
                <option value="AUDIO_EQUIPMENT">Audio Equipment</option>
                <option value="CABLES">Cables</option>
                <option value="CIRCUIT_BOARDS">Circuit Boards</option>
                <option value="GAME_CONSOLES">Game Consoles</option>
                <option value="CAMERAS">Cameras</option>
                <option value="POWER_STRIPS">Power Strips</option>
                <option value="REFRIGERATORS">Refrigerators</option>
              </select>
            </div>

            {/* üì∏ Image Upload */}
            <div className="flex flex-col">
              <label className="text-gray-700 mb-2">Upload Image of Item(s)</label>
              <input
                type="file"
                accept="image/*"
                ref={fileInputRef}
                onChange={handleImageChange}
                className="p-2 border rounded"
                required
              />
              {imagePreview && (
                <img src={imagePreview} alt="Preview" className="mt-4 w-40 h-40 object-cover rounded" />
              )}
            </div>

            {/* Pickup charge info */}
            <div className="text-sm text-gray-600 mt-2">
              {userRole === 'charity' ? (
                <span className="text-green-600 font-semibold">No pickup charge for charity organizations.</span>
              ) : (
                <span>Pickup charge: ‚Çπ40 (via Razorpay)</span>
              )}
            </div>

            {/* Submit Button */}
            <div className="mt-4">
              <button
                type="submit"
                disabled={loading}
                className="w-full bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded"
              >
                {loading ? 'Processing...' : 'Schedule Pickup'}
              </button>
            </div>
          </form>
        </div>
      </main>
    </div>
  );
}

export default Pickup;
