// src/pages/WorkerLogin.js
import React, { useState } from 'react';
import { loginWorker } from '../services/workerApi';
import { useNavigate } from 'react-router-dom'; // Assuming you use react-router-dom for navigation
import './Login.css'; // You can reuse your existing Login.css or create a new one

const WorkerLogin = () => {
    const [identifier, setIdentifier] = useState(''); // This will be the username for workers
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');
        try {
            const response = await loginWorker({ identifier, password });
            setMessage(response.message); // "Worker login successful"
            // Store the token (e.g., in localStorage)
            localStorage.setItem('workerToken', response.token);
            localStorage.setItem('workerRole', response.role); // Store the role if needed
            localStorage.setItem('workerUsername', identifier); // Store username for display if needed

            // Redirect to a worker dashboard or home page
            navigate('/worker/dashboard'); // Create a /worker/dashboard route later
        } catch (err) {
            setError(err || 'Login failed. Please check your credentials.');
        }
    };

    return (
        <div className="login-container">
            <h2>Worker Login</h2>
            <form onSubmit={handleSubmit} className="login-form">
                <div className="form-group">
                    <label htmlFor="identifier">Username:</label>
                    <input
                        type="text"
                        id="identifier"
                        value={identifier}
                        onChange={(e) => setIdentifier(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" className="login-button">Login</button>
            </form>
            {message && <p className="success-message">{message}</p>}
            {error && <p className="error-message">{error}</p>}
        </div>
    );
};

export default WorkerLogin;
