import { useState } from "react";
import axios from "axios";

const Maps = () => {
  const [address, setAddress] = useState("");
  const [location, setLocation] = useState(null);
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);

  const apiKey = import.meta.env.VITE_GOMAPS_API_KEY;

  const handleUseMyLocation = () => {
    if (!navigator.geolocation) {
      alert("Geolocation is not supported by your browser.");
      return;
    }
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setLocation({ lat: latitude, lng: longitude });
        handleSearch(latitude, longitude);
      },
      () => {
        alert("Unable to retrieve your location.");
      }
    );
  };

  const handleSearch = async (lat = null, lng = null) => {
    setLoading(true);
    try {
      let locationString = lat && lng ? `${lat},${lng}` : undefined;
      const response = await axios.get(
        "https://maps.gomaps.pro/maps/api/place/textsearch/json",
        {
          params: {
            query: "e-waste center",
            location: locationString,
            radius: 5000,
            language: "en",
            key: apiKey,
          },
        }
      );
      setPlaces(response.data.results);
    } catch (error) {
      console.error("Search failed", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4 text-center">Find E-Waste Centers</h1>

      <div className="flex flex-col sm:flex-row items-center gap-4 mb-6">
        <input
          type="text"
          className="w-full sm:flex-1 px-4 py-2 border rounded shadow"
          placeholder="Enter your location (e.g., Bhopal)"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />
        <button
          onClick={() => handleSearch()}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Search
        </button>
        <button
          onClick={handleUseMyLocation}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Use My Location
        </button>
      </div>

      {loading ? (
        <p className="text-center">Searching...</p>
      ) : (
        <ul className="space-y-4">
          {places.map((place, index) => (
            <li key={index} className="p-4 border rounded bg-white shadow">
              <h3 className="font-semibold">{place.name}</h3>
              <p>{place.formatted_address}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Maps;
