import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function Login() {
  const [identifier, setIdentifier] = useState('');
  const [password, setPassword] = useState('');
  // Set the default role to 'individual'
  const [role, setRole] = useState('individual');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8082/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ identifier, password, role }),
      });

      const data = await response.json();

      if (response.ok) {
        // ✅ Save login status, userId, login time, and role
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('userId', data.userId);
        localStorage.setItem('loginTime', Date.now().toString());
        localStorage.setItem('role', role); // Store the selected/default role

        // ✅ Notify app (especially Header) about login status change
        window.dispatchEvent(new Event('loginStatusChanged'));

        // ✅ Navigate to profile
        navigate('/profile');
      } else {
        setMessage(data.message || 'Login failed');
      }
    } catch (error) {
      console.error('Login error:', error);
      setMessage('An error occurred. Try again later.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-green-50 px-4">
      <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-md">
        <h2 className="text-3xl font-extrabold text-green-800 mb-6 text-center">Login</h2>
        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label className="block text-gray-700 mb-1">Email / Phone Number</label>
            <input
              type="text"
              name="identifier"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-green-500"
              value={identifier}
              onChange={(e) => setIdentifier(e.target.value)}
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 mb-1">Password</label>
            <input
              type="password"
              name="password"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-green-500"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="mb-6">
            <label className="block text-gray-700 mb-1">Select Role</label>
            <select
              name="role"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-green-500"
              value={role} // Bind value to state
              onChange={(e) => setRole(e.target.value)}
              required
            >
              <option value="">Select your role</option> {/* Keep this for choice */}
              <option value="individual">Individual</option>
              <option value="commercial">Commercial</option>
              <option value="charity">Charity</option>
            </select>
          </div>
          <button
            type="submit"
            className="w-full bg-green-700 hover:bg-green-800 text-white p-3 rounded-lg font-medium"
          >
            Login
          </button>
        </form>

        {message && (
          <p className="text-center text-red-600 mt-4">{message}</p>
        )}

        <p className="mt-6 text-sm text-center text-gray-600">
          Don’t have an account?{' '}
          <Link to="/register" className="text-green-700 font-medium hover:underline">
            Register here
          </Link>
        </p>
      </div>
    </div>
  );
}

export default Login;
