// src/services/workerApi.js (Example - you need to implement the actual API calls)
import api from './api'; // Assuming you have an axios instance or similar

export const loginWorker = async (credentials) => {
    const response = await api.post('/worker/login', credentials);
    return response.data;
};

// ... other existing functions like getWorkerProfile, getWorkerPickupRequests, etc.

export const getUnassignedPickups = async () => {
    const response = await api.get('/pickups/unassigned'); // Example endpoint
    return response.data;
};

export const assignPickup = async (pickupId) => {
    const response = await api.post(`/pickups/${pickupId}/assign`); // Example endpoint
    return response.data;
};

export const updatePickupRequestStatus = async (pickupId, statusData) => {
    const response = await api.put(`/pickups/${pickupId}/status`, statusData);
    return response.data;
};

export const getWorkerProfile = async () => {
    const response = await api.get('/worker/profile');
    return response.data;
};

export const getWorkerPickupRequests = async () => {
    const response = await api.get('/worker/pickups/assigned');
    return response.data;
};

export const getTodaysPendingPickups = async () => {
    const response = await api.get('/worker/pickups/pending-today');
    return response.data;
};

export const getAllWorkerPickupLogs = async () => {
    const response = await api.get('/worker/pickup-logs');
    return response.data;
};
