// src/components/ProtectedRoute.js
import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';

const ProtectedRoute = ({ allowedRoles }) => {
    // Check for user login
    const userToken = localStorage.getItem('token');
    const userRole = localStorage.getItem('role');

    // Check for worker login
    const workerToken = localStorage.getItem('workerToken');
    const workerRole = localStorage.getItem('workerRole');

    let isAuthenticated = false;
    let actualRole = null;

    if (workerToken && workerRole === 'WORKER') {
        isAuthenticated = true;
        actualRole = 'WORKER';
    } else if (userToken && userRole) {
        isAuthenticated = true;
        actualRole = userRole;
    }

    if (!isAuthenticated) {
        return <Navigate to="/login" replace />;
    }

    // If isAuthenticated, but specific roles are required
    if (allowedRoles && allowedRoles.length > 0) {
        if (!actualRole || !allowedRoles.includes(actualRole)) {
            console.warn(`Access denied: User role '${actualRole}' not in allowed roles: ${allowedRoles.join(', ')}`);

            // --- SMART REDIRECTION (Crucial for worker issue) ---
            if (actualRole === 'WORKER') {
                return <Navigate to="/worker/dashboard" replace />;
            } else if (['individual', 'commercial', 'charity'].includes(actualRole)) {
                return <Navigate to="/profile" replace />;
            }
            return <Navigate to="/" replace />; // Fallback
        }
    }

    return <Outlet />;
};

export default ProtectedRoute;
