import React, { useEffect, useRef, useState } from 'react';

// Define your Google Maps API Key here.
// IMPORTANT: Replace 'YOUR_Maps_API_KEY' with your actual, full, and activated key.
// Google Maps Platform requires billing enabled even for its free tier.
const Maps_API_KEY = 'AlzaSya5QD-sqq93fLWzpsX9LYdlTZqugV3vLXQ';

const MapsLeaflet = () => { // Function name is MapsLeaflet as requested
  const mapRef = useRef(null);
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(true);
  const [userDeviceLocation, setUserDeviceLocation] = useState(null); // [lat, lng] from navigator.geolocation
  // The center of the map/search, can be user's device location or manually geocoded location
  const [currentSearchCenter, setCurrentSearchCenter] = useState(null); 
  const [manualLocationInput, setManualLocationInput] = useState('');
  const [mapInstance, setMapInstance] = useState(null); // State to hold the Google Map instance

  // Function to load the Google Maps API script dynamically
  const loadGoogleMapsScript = (callback) => {
    // Check if Google Maps API is already loaded
    if (window.google && window.google.maps) {
      callback();
      return;
    }

    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=${Maps_API_KEY}&libraries=places`;
    script.async = true;
    script.defer = true;
    script.onload = callback; // Call the callback when script is loaded
    script.onerror = () => {
      console.error('Failed to load Google Maps script. Check API key or network.');
      setLoading(false);
      alert('Failed to load map services. Please check your internet connection or API key.');
    };
    document.body.appendChild(script);
  };

  // Function to initialize the map and perform search based on coordinates
  const initializeMapAndSearch = (lat, lng) => {
    setLoading(true);
    setPlaces([]); // Clear previous places

    const mapOptions = {
      center: { lat: lat, lng: lng },
      zoom: 14,
      mapTypeId: window.google.maps.MapTypeId.ROADMAP // ROADMAP, SATELLITE, HYBRID, TERRAIN
    };

    // Create the map instance
    const map = new window.google.maps.Map(mapRef.current, mapOptions);
    setMapInstance(map); // Store map instance

    // Marker for the center of the search (could be user's or manual input)
    new window.google.maps.Marker({
      position: { lat: lat, lng: lng },
      map,
      title: 'Search Center',
      icon: 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png', // Blue for search center
    });

    // --- Google Places Nearby Search ---
    const service = new window.google.maps.places.PlacesService(map);
    const request = {
      location: { lat: lat, lng: lng },
      radius: 10000, // 10 km radius
      // You can use 'type' for predefined categories or 'keyword' for more general terms.
      // 'store' is a general commercial type, 'point_of_interest' is broader.
      type: ['store', 'point_of_interest'], 
      keyword: 'e-waste recycling, electronics waste, scrap dealer, kabadiwala', 
    };

    service.nearbySearch(request, (results, status) => {
      if (status === window.google.maps.places.PlacesServiceStatus.OK && results) {
        console.log('Google Places Nearby results:', results);
        setPlaces(results);

        // Add markers for nearby places
        results.forEach((place) => {
          if (!place.geometry || !place.geometry.location) return; // Skip if no location

          const marker = new window.google.maps.Marker({
            map,
            position: place.geometry.location,
            title: place.name,
            icon: 'http://maps.google.com/mapfiles/ms/icons/green-dot.png', // Green for results
          });

          // Info window for each place
          const infoWindow = new window.google.maps.InfoWindow({
            content: `<strong>${place.name}</strong><br>${place.vicinity || place.formatted_address || 'Address not available'}`,
          });

          marker.addListener('click', () => {
            infoWindow.open(map, marker);
          });
        });
        setLoading(false);
      } else {
        console.warn('Google Places Nearby Search failed or found no results:', status);
        alert(`No nearby e-waste centers or kabadiwale found. Status: ${status}`);
        setLoading(false);
      }
    });
  };

  // Function to geocode a manual location input using Google Geocoding Service
  const geocodeManualLocation = async (address) => {
    setLoading(true);
    setPlaces([]); // Clear previous results
    
    // Google Geocoding API requires the Maps JS API to be loaded first.
    if (!window.google || !window.google.maps || !window.google.maps.Geocoder) {
      console.error('Google Maps Geocoder not available.');
      alert('Map services not fully loaded. Please wait and try again.');
      setLoading(false);
      return;
    }

    const geocoder = new window.google.maps.Geocoder();
    geocoder.geocode({ address: address }, (results, status) => {
      if (status === window.google.maps.GeocoderStatus.OK && results[0]) {
        const lat = results[0].geometry.location.lat();
        const lng = results[0].geometry.location.lng();
        const newCenter = { lat, lng };
        setCurrentSearchCenter(newCenter);
        initializeMapAndSearch(lat, lng); // Re-initialize map and search with new center
      } else {
        console.error('Geocoding failed:', status);
        alert(`Location not found: ${status}. Please try a more specific address.`);
        setLoading(false);
        setPlaces([]);
      }
    });
  };

  // Handles click for manual location search
  const handleManualSearchClick = () => {
    if (manualLocationInput.trim()) {
      geocodeManualLocation(manualLocationInput.trim());
    } else {
      alert('Please enter a location to search.');
    }
  };

  // Handles search for current location (via device geolocation)
  const handleCurrentLocationSearchClick = () => {
    setLoading(true);
    setPlaces([]); // Clear previous results

    if (!navigator.geolocation) {
      alert('Geolocation is not supported by your browser. Please enter a location manually.');
      setLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        const { latitude, longitude } = pos.coords;
        const newCenter = { lat: latitude, lng: longitude };
        setUserDeviceLocation(newCenter); // Store device location
        setCurrentSearchCenter(newCenter); // Set search center to device location
        initializeMapAndSearch(latitude, longitude); // Initialize map and search
      },
      (err) => {
        console.error('Geolocation error:', err);
        setLoading(false);
        alert('Could not get your current location. Please ensure location services are enabled or try searching manually.');
        // If geolocation fails, set a default center if none exists, but don't search automatically.
        if (!currentSearchCenter) {
          setCurrentSearchCenter({ lat: 18.5204, lng: 73.8567 }); // Default to Pune
        }
      },
      { enableHighAccuracy: true, timeout: 5000, maximumAge: 0 }
    );
  };

  // Initial load effect: load Google Maps script, then try current location search
  useEffect(() => {
    loadGoogleMapsScript(() => {
      // Once Google Maps API is loaded, then attempt to get user location
      handleCurrentLocationSearchClick();
    });
  }, []); // Runs once on component mount

  // Determine the map's initial center for the MapContainer
  const mapDisplayCenter = currentSearchCenter || { lat: 18.5204, lng: 73.8567 }; // Default to Pune if no location is found yet

  if (loading && !currentSearchCenter) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', fontSize: '1.2rem' }}>
        <p>üó∫Ô∏è Loading Google Maps... Please grant location access or search manually.</p>
      </div>
    );
  }

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      {/* Sidebar for controls and displaying nearby places */}
      <div
        style={{
          width: '30%',
          padding: '1rem',
          overflowY: 'auto',
          backgroundColor: '#f9f9f9',
          borderRight: '1px solid #ddd',
          boxSizing: 'border-box',
        }}
      >
        <h2>Search E-Waste Centers (Google Maps)</h2>

        {/* Manual Location Input */}
        <div style={{ marginBottom: '1rem', borderBottom: '1px solid #eee', paddingBottom: '1rem' }}>
          <h3>Search by Location:</h3>
          <input
            type="text"
            placeholder="Enter city, address, or landmark (e.g., Pune, Mumbai)"
            value={manualLocationInput}
            onChange={(e) => setManualLocationInput(e.target.value)}
            onKeyPress={(e) => {
              if (e.key === 'Enter') {
                handleManualSearchClick();
              }
            }}
            style={{ width: 'calc(100% - 10px)', padding: '8px', marginBottom: '10px', borderRadius: '4px', border: '1px solid #ccc' }}
          />
          <button
            onClick={handleManualSearchClick}
            disabled={loading}
            style={{ padding: '8px 15px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer', opacity: loading ? 0.7 : 1 }}
          >
            Search Location
          </button>
        </div>

        {/* Current Location Search Button */}
        <div style={{ marginBottom: '1rem', borderBottom: '1px solid #eee', paddingBottom: '1rem' }}>
          <h3>Or Search My Current Location:</h3>
          <button
            onClick={handleCurrentLocationSearchClick}
            disabled={loading}
            style={{ padding: '8px 15px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer', opacity: loading ? 0.7 : 1 }}
          >
            Use My Location
          </button>
        </div>
        
        {/* Nearby Centers List */}
        <h3>Nearby Centers List:</h3>
        {loading ? (
          <p>üîç Searching for centers...</p>
        ) : places.length === 0 ? (
          <p>üòî No nearby centers found based on keywords for the current location. Try a different location or broader terms.</p>
        ) : (
          places.map((place, idx) => (
            <div
              key={idx}
              style={{
                marginBottom: '10px',
                padding: '10px',
                backgroundColor: '#fff',
                borderRadius: '6px',
                boxShadow: '0 0 5px rgba(0,0,0,0.1)',
                cursor: 'pointer',
                transition: 'transform 0.2s ease-in-out',
              }}
              onClick={() => {
                // Pan map to the selected place's location
                if (mapInstance && place.geometry && place.geometry.location) {
                    mapInstance.panTo(place.geometry.location);
                    mapInstance.setZoom(16); // Zoom in
                }
              }}
              onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-2px)'}
              onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
            >
              <h4>{place.name || 'Unknown Place'}</h4> 
              <p>{place.vicinity || place.formatted_address || 'Address not available'}</p>
              {place.plus_code && <p>Plus Code: {place.plus_code.compound_code}</p>}
              {place.opening_hours && place.opening_hours.open_now !== undefined && (
                <p>Status: {place.opening_hours.open_now ? 'Open Now' : 'Closed'}</p>
              )}
              {/* Google Places results have more rich data, explore 'place' object */}
            </div>
          ))
        )}
      </div>

      {/* Map Display Area */}
      <div style={{ width: '70%' }}>
        {/* The map will be rendered inside this div */}
        <div ref={mapRef} style={{ width: '100%', height: '100%' }} />
      </div>
    </div>
  );
};

export default MapsLeaflet;
