// src/services/workerApi.js
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8082/api';

const axiosInstance = axios.create({
    baseURL: API_BASE_URL,
    headers: {
        'Content-Type': 'application/json',
    },
});

axiosInstance.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('workerToken');
        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

axiosInstance.interceptors.response.use(
    (response) => {
        return response;
    },
    (error) => {
        if (error.response && error.response.status === 401) {
            console.error("Authentication expired or invalid. Redirecting to worker login.");
            localStorage.removeItem('workerToken');
            // You might want to trigger a global event or navigate here,
            // but for simplicity, we'll let the component handle navigation.
        }
        return Promise.reject(error);
    }
);

// Worker Registration
export const registerWorker = async (workerData) => {
    try {
