import axios from 'axios';

const API_BASE_URL = 'http://localhost:8082';

const api = axios.create({
    baseURL: API_BASE_URL,
    headers: {
        'Content-Type': 'application/json',
    },
});

// Request Interceptor: Attach JWT token to every outgoing request
api.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('token');
        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

// Response Interceptor: Handle token expiration or unauthorized responses
api.interceptors.response.use(
    (response) => response,
    (error) => {
        // If it's a 401 (Unauthorized) or 403 (Forbidden) response
        if (error.response && (error.response.status === 401 || error.response.status === 403)) {
            console.error('API Error: Unauthorized or Forbidden. Logging out.');
            // Dispatch a custom event to notify Header/other components
            window.dispatchEvent(new Event('loginStatusChanged'));
            // Optionally, redirect to login page (can be handled globally or in specific components)
            // window.location.href = '/login'; // Or use navigate from a react-router context
            // It's better to let components handle logout/redirect on their own error
        }
        return Promise.reject(error);
    }
);

export default api; // Export the configured Axios instance
