// Create an Axios instance to centralize configuration and add interceptors
const axiosInstance = axios.create({
    baseURL: API_BASE_URL,
    headers: {
        'Content-Type': 'application/json',
    },
});

// Request Interceptor: Add the JWT token to every outgoing request
axiosInstance.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('workerToken'); // Retrieve the worker's token
        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
    },
    (error) => {
        // Do something with request error
        return Promise.reject(error);
    }
);

// Response Interceptor (Optional but Recommended):
// Can be used to handle global errors (e.g., 401 Unauthorized globally)
axiosInstance.interceptors.response.use(
    (response) => {
        return response;
    },
    (error) => {
        // Example: If a 401 Unauthorized is received, redirect to login
        if (error.response && error.response.status === 401) {
            console.error("Authentication expired or invalid. Redirecting to worker login.");
            localStorage.removeItem('workerToken'); // Clear invalid token
            // This assumes you have access to navigate or a global event bus for redirection
