import axios from 'axios';

// Base URL for your backend API
export const API_BASE_URL = 'http://localhost:8082'; // Ensure this matches your backend URL

const api = axios.create({
    baseURL: API_BASE_URL,
    headers: {
        'Content-Type': 'application/json',
    },
});

// Request Interceptor: Attaches the user's JWT token to every outgoing request
api.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('token'); // Get the token stored after user login
        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

// Response Interceptor: Handles cases of unauthorized or forbidden responses (e.g., token expired)
api.interceptors.response.use(
    (response) => response, // If the response is successful, just return it
    (error) => {
        // If the response status is 401 (Unauthorized) or 403 (Forbidden)
        if (error.response && (error.response.status === 401 || error.response.status === 403)) {
            console.error('API Error: Unauthorized or Forbidden. Attempting logout.');
            // Dispatch a custom event to notify other parts of the application (e.g., Header component)
            // that the login status might have changed, triggering a logout flow.
            window.dispatchEvent(new Event('loginStatusChanged'));
            // Note: Actual navigation to /login should happen in a component listening to this event,
            // or in a global error handler if you implement one in your App.js/Router setup.
        }
        return Promise.reject(error); // Propagate the error further
    }
);

export default api; // Export the configured Axios instance for use in other components
