// src/components/ProtectedRoute.js
import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';

const ProtectedRoute = ({ allowedRoles }) => {
    // Check for user login (assuming your user login stores 'token' and 'role')
    const userToken = localStorage.getItem('token'); // Assuming general user token is 'token'
    const userRole = localStorage.getItem('role');   // Assuming general user role is 'role'

    // Check for worker login (assuming worker login stores 'workerToken' and 'workerRole')
    const workerToken = localStorage.getItem('workerToken');
    const workerRole = localStorage.getItem('workerRole');

    let isAuthenticated = false;
    let actualRole = null;

    // Prioritize worker authentication if a worker token exists
    if (workerToken && workerRole === 'WORKER') {
        isAuthenticated = true;
        actualRole = 'WORKER';
    } else if (userToken && userRole) { // Otherwise, check for general user token
        isAuthenticated = true;
        actualRole = userRole; // Use the specific role for general users
    }

    if (!isAuthenticated) {
        // If not authenticated at all, redirect to a generic login (or home)
        return <Navigate to="/login" replace />;
    }

    // If isAuthenticated, but specific roles are required
    if (allowedRoles && allowedRoles.length > 0) {
        if (!actualRole || !allowedRoles.includes(actualRole)) {
            // User is authenticated but doesn't have the required role
            console.warn(`Access denied: User role '${actualRole}' not in allowed roles: ${allowedRoles.join(', ')}`);
            return <Navigate to="/" replace />; // Redirect to home or an unauthorized page
        }
    }

    // If authenticated and roles match (or no specific roles required), render children
    return <Outlet />;
};

export default ProtectedRoute;
