import React, { useState, useEffect } from "react";
import axios from "axios";

const Maps = () => {
  const [addressInput, setAddressInput] = useState(""); // For manual address input
  const [currentMapCenter, setCurrentMapCenter] = useState(null); // Center of the map {lat, lng} (user's location)
  const [places, setPlaces] = useState([]); // Results from GoMaps API
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("e-waste center"); // Default search query
  const [selectedPlace, setSelectedPlace] = useState(null); // The place for which directions are requested
  const [routeData, setRouteData] = useState(null); // Stores route details from GoMaps Directions API

  // Directly integrating the GoMaps API Key
  const GOMAPS_API_KEY = 'AlzaSya5QD-sqq93fLWzpsX9LYdlTZqugV3vLXQ';

  // Default center for the map if no location is available (Pune, India)
  const defaultMapCenter = { lat: 18.5204, lng: 73.8567 };

  // --- Automatic Location on Component Mount ---
  useEffect(() => {
    // Attempt to get user's location on component mount
    handleUseMyLocation();
  }, []); // Empty dependency array means this runs once on mount

  // --- Location Handling Functions ---

  // Handler for getting "My Location"
  const handleUseMyLocation = () => {
    setLoading(true);
    setError(null);
    setPlaces([]); // Clear previous places
    setRouteData(null); // Clear previous route
    setSelectedPlace(null); // Clear selected place

    if (!navigator.geolocation) {
      setError("Geolocation is not supported by your browser. Please enter an address manually.");
      setLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setCurrentMapCenter({ lat: latitude, lng: longitude });
        setAddressInput(""); // Clear manual address input if using current location
        handleSearch(latitude, longitude); // Immediately search with current location
      },
      (err) => {
        console.error("Unable to retrieve your location:", err);
        if (err.code === err.PERMISSION_DENIED) {
          setError("Location access denied. Please enable location services in your browser settings or enter an address manually.");
        } else {
          setError("Unable to retrieve your location. Please try again or enter an address manually.");
        }
        setLoading(false);
      }
    );
  };

  // Handler for "Search by Address" button (fallback/alternative)
  const handleManualAddressSearch = async () => {
    if (!addressInput.trim()) {
      setError("Please enter an address to search.");
      return;
    }
    setLoading(true);
    setError(null);
    setPlaces([]); // Clear previous places
    setRouteData(null); // Clear previous route
    setSelectedPlace(null); // Clear selected place
    setCurrentMapCenter(null); // Clear map center until new location is found

    try {
      // First, get coordinates for the entered address using GoMaps.pro FindPlaceFromText
      const geoResponse = await axios.get(
        "https://maps.gomaps.pro/maps/api/place/findplacefromtext/json",
        {
          params: {
            input: addressInput,
            inputtype: "textquery",
            fields: "geometry", // Request geometry for lat/lng
            key: GOMAPS_API_KEY,
          },
        }
      );

      if (geoResponse.data.status === 'OK' && geoResponse.data.candidates.length > 0) {
        const { lat, lng } = geoResponse.data.candidates[0].geometry.location;
        setCurrentMapCenter({ lat, lng });
        handleSearch(lat, lng); // Then search for places near these coordinates
      } else {
        setError(`Could not find coordinates for "${addressInput}". Status: ${geoResponse.data.status}`);
        setLoading(false);
      }

    } catch (error) {
      console.error("Geocoding or search failed", error);
      setError("Failed to resolve address or search for places.");
      setLoading(false);
    }
  };

  // Main search function for e-waste centers, using either provided lat/lng or currentMapCenter
  const handleSearch = async (lat = null, lng = null) => {
    setLoading(true);
    setError(null);
    setPlaces([]); // Clear previous results

    const searchLat = lat || currentMapCenter?.lat;
    const searchLng = lng || currentMapCenter?.lng;

    if (!searchLat || !searchLng) {
      setError("No location available. Please enable location services or enter an address.");
      setLoading(false);
      return;
    }

    try {
      // Using Nearby Search API from GoMaps.pro
      const response = await axios.get(
        "https://maps.gomaps.pro/maps/api/place/nearbysearch/json",
        {
          params: {
            keyword: searchQuery, // Use the state variable for dynamic query
            location: `${searchLat},${searchLng}`,
            radius: 50000, // Search within 50 km radius (adjust as needed)
            language: "en",
            key: GOMAPS_API_KEY,
          },
        }
      );

      if (response.data.status === 'OK') {
        setPlaces(response.data.results);
      } else {
        setError(`Error from GoMaps Nearby Search API: ${response.data.status}`);
      }
    } catch (error) {
      console.error("Search failed", error);
      setError(`Search failed: ${error.message || 'Network error'}`);
    } finally {
      setLoading(false);
    }
  };

  // --- Directions Function ---
  const getDirections = async (destinationPlace) => {
    if (!currentMapCenter) {
      setError("Your current location is not available to calculate directions.");
      return;
    }
    setLoading(true);
    setError(null);
    setRouteData(null); // Clear previous route
    setSelectedPlace(destinationPlace); // Set the selected place for map marker

    try {
      const origin = `${currentMapCenter.lat},${currentMapCenter.lng}`;
      const destination = `${destinationPlace.geometry.location.lat},${destinationPlace.geometry.location.lng}`;

      // Using GoMaps.pro Directions API
      const response = await axios.get(
        "https://maps.gomaps.pro/maps/api/directions/json",
        {
          params: {
            origin: origin,
            destination: destination,
            key: GOMAPS_API_KEY,
          },
        }
      );

      if (response.data.status === 'OK' && response.data.routes.length > 0) {
        setRouteData(response.data.routes[0]); // Store the first (most relevant) route
      } else {
        setError(`Could not find directions. Status: ${response.data.status}`);
      }
    } catch (error) {
      console.error("Directions failed", error);
      setError(`Failed to get directions: ${error.message || 'Network error'}`);
    } finally {
      setLoading(false);
    }
  };

  // --- Hypothetical GoMaps.pro Static Map URL Generation ---
  // THIS IS CONJECTURAL AND WILL LIKELY NOT WORK WITHOUT ACTUAL GOMAPS.PRO STATIC MAPS API DOCS
  const getStaticMapUrl = () => {
    // Use user's current location or default if nothing is set
    const mapCenter = currentMapCenter || defaultMapCenter;

    let markers = [];
    // Marker for user's current location
    if (currentMapCenter) {
      markers.push(`markers=color:blue|label:You|${currentMapCenter.lat},${currentMapCenter.lng}`);
    }
    // Marker for the selected e-waste center (destination)
    if (selectedPlace) {
      markers.push(`markers=color:green|label:Dest|${selectedPlace.geometry.location.lat},${selectedPlace.geometry.location.lng}`);
    } else {
      // If no specific place selected for route, add markers for found places
      places.forEach((place, index) => {
        if (index < 5) { // Limit to first 5 places for static map, or it gets cluttered/URL too long
          markers.push(`markers=color:red|label:${index + 1}|${place.geometry.location.lat},${place.geometry.location.lng}`);
        }
      });
    }

    let polylinePath = '';
    // Add polyline if route data is available
    if (routeData && routeData.overview_polyline && routeData.overview_polyline.points) {
      // Static Map APIs often expect an 'enc:' prefix for encoded polylines
      polylinePath = `&path=color:0x0000FF|weight:5|enc:${encodeURIComponent(routeData.overview_polyline.points)}`;
    }

    // THIS IS A HYPOTHETICAL GOMAPS.PRO STATIC MAP API URL FORMAT.
    // It is based on common static map API patterns (e.g., Google Static Maps API)
    // and is UNLIKELY TO WORK without GoMaps.pro's OWN DOCUMENTATION.
    // Replace 'https://maps.gomaps.pro/maps/api/staticmap' with their actual endpoint,
    // and adjust parameters like 'center', 'zoom', 'size', 'markers', 'path'
    // based on their documentation.
    return `https://maps.gomaps.pro/maps/api/staticmap?center=${mapCenter.lat},${mapCenter.lng}&zoom=14&size=600x400&${markers.join('&')}${polylinePath}&key=${GOMAPS_API_KEY}`;
  };

  return (
    <div className="min-h-[calc(100vh-120px)] bg-gray-100 p-8 flex flex-col items-center">
      <h1 className="text-4xl font-extrabold text-center text-gray-900 mb-10">
        Find Nearby Services
      </h1>

      <div className="flex flex-col lg:flex-row gap-8 w-full max-w-7xl">
        {/* Left Side: Location Input and Search Results */}
        <div className="w-full lg:w-1/3 p-4 bg-white rounded-lg shadow-lg flex flex-col">
          <h2 className="text-2xl font-bold mb-6 text-gray-800 text-center">Search Options</h2>

          {/* Search Query Input */}
          <div className="mb-4">
            <label htmlFor="search-query" className="block text-gray-700 text-sm font-bold mb-2">
              What are you looking for?
            </label>
            <input
              type="text"
              id="search-query"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="e.g., e-waste center, kabaddi wale"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              disabled={loading}
            />
          </div>

          {/* Manual Address Input (Fallback/Alternative) */}
          <div className="mb-4">
            <label htmlFor="address-input" className="block text-gray-700 text-sm font-bold mb-2">
              Or Enter Location Manually:
            </label>
            <input
              type="text"
              id="address-input"
              className="w-full px-4 py-2 border rounded shadow"
              placeholder="e.g., Pune, India"
              value={addressInput}
              onChange={(e) => setAddressInput(e.target.value)}
              disabled={loading}
            />
          </div>

          {/* Search Buttons */}
          <div className="flex flex-col sm:flex-row gap-4 mb-6">
            <button
              onClick={handleManualAddressSearch}
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 w-full sm:flex-1"
              disabled={loading}
            >
              Search by Address
            </button>
            <button
              onClick={handleUseMyLocation}
              className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 w-full sm:flex-1"
              disabled={loading}
            >
              Use My Location
            </button>
          </div>

          {/* Messages */}
          {loading && <p className="text-blue-600 text-center mt-4">Loading...</p>}
          {error && <p className="text-red-600 text-center mt-4">{error}</p>}
          {currentMapCenter && (
            <p className="text-sm text-gray-600 mt-4 text-center">
              Your Location: {currentMapCenter.lat?.toFixed(4)}, {currentMapCenter.lng?.toFixed(4)}
            </p>
          )}

          {/* Search Results Display */}
          {places.length > 0 && (
            <div className="mt-6 border-t border-gray-200 pt-6 flex-grow overflow-y-auto max-h-[300px]">
              <h2 className="text-xl font-semibold mb-4 text-gray-800">Found Places:</h2>
              <ul className="space-y-3">
                {places.map((place) => (
                  <li key={place.place_id} className="p-3 border border-gray-200 rounded-md bg-white shadow-sm">
                    <h3 className="font-bold text-gray-900">{place.name}</h3>
                    <p className="text-sm text-gray-600">{place.vicinity || place.formatted_address}</p>
                    {place.rating && (
                      <p className="text-xs text-gray-700">Rating: {place.rating} ({place.user_ratings_total || 0} reviews)</p>
                    )}
                    {place.opening_hours && place.opening_hours.open_now !== undefined && (
                      <p className={`text-xs ${place.opening_hours.open_now ? 'text-green-700' : 'text-red-700'}`}>
                        {place.opening_hours.open_now ? 'Open Now' : 'Closed'}
                      </p>
                    )}
                    <button
                      onClick={() => getDirections(place)}
                      className="mt-2 bg-purple-600 text-white px-3 py-1 rounded text-sm hover:bg-purple-700"
                      disabled={loading}
                    >
                      Get Directions
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          )}
          {places.length === 0 && !loading && !error && currentMapCenter && (
            <p className="text-gray-600 text-center mt-4">No results found for "{searchQuery}" near your location.</p>
          )}
        </div>

        {/* Right Side: Static Map Image Display Area & Route Steps */}
        <div className="w-full lg:w-2/3 h-[600px] rounded-lg shadow-lg overflow-hidden flex flex-col bg-gray-200">
            {/* Static Map Image */}
            <div className="flex-shrink-0 h-[400px] w-full flex items-center justify-center bg-gray-300">
                {getStaticMapUrl() ? (
                    <img
                        src={getStaticMapUrl()}
                        alt="GoMaps.pro Static Map with Route"
                        className="w-full h-full object-cover"
                        onError={(e) => {
                            e.target.onerror = null; // Prevents infinite loop
                            e.target.src = "https://placehold.co/600x400/cccccc/333333?text=Static+Map+Unavailable";
                            console.error("Failed to load static map image. GoMaps.pro Static Maps API might not exist or URL format is incorrect.");
                        }}
                    />
                ) : (
                    <p className="text-gray-500 text-center">Select a location or get directions to display a static map.</p>
                )}
            </div>

            {/* Route Steps Display */}
            {routeData && (
                <div className="p-4 bg-white flex-grow overflow-y-auto">
                    <h2 className="text-xl font-semibold mb-3 text-gray-800">
                        Directions to {selectedPlace?.name}
                    </h2>
                    <p className="text-sm text-gray-700 mb-2">
                        Distance: {routeData.legs[0]?.distance?.text || 'N/A'}, Duration: {routeData.legs[0]?.duration?.text || 'N/A'}
                    </p>
                    <ul className="space-y-2">
                        {routeData.legs[0]?.steps.map((step, index) => (
                            <li key={index} className="text-sm text-gray-800" dangerouslySetInnerHTML={{ __html: `${index + 1}. ${step.html_instructions}` }}>
                            </li>
                        ))}
                    </ul>
                    {/* You could add a link to open in a full map app if a deep link URL format is available from GoMaps.pro */}
                    {currentMapCenter && selectedPlace && (
                        <a
                            href={`https://maps.gomaps.pro/directions?origin=${currentMapCenter.lat},${currentMapCenter.lng}&destination=${selectedPlace.geometry.location.lat},${selectedPlace.geometry.location.lng}&key=${GOMAPS_API_KEY}`}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="mt-4 inline-block bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 text-sm"
                        >
                            View Full Route (GoMaps.pro - if supported)
                        </a>
                    )}
                </div>
            )}
        </div>
      </div>
    </div>
  );
};

export default Maps;
