import api from './api'; // Assuming 'api' is your Axios instance

// Fetches the closest unassigned pickup for the current worker
export const getClosestUnassignedPickup = () => {
    return api.get('/worker/pickups/unassigned/closest');
};

// Fetches today's pending/assigned pickups for the worker
export const getTodaysPendingPickupsForWorker = () => {
    return api.get('/worker/pickups/today/pending');
};

// Fetches all assigned pickups (history) for the worker
export const getWorkerPickupHistory = () => { // Renamed from getAllWorkerPickups
    return api.get('/worker/pickups');
};

// Assigns a pickup to the current worker
export const assignPickupToWorker = (pickupId) => {
    return api.post(`/worker/pickups/${pickupId}/assign`);
};

// Updates the status of a pickup
export const updatePickupStatus = (pickupId, updateData) => {
    // updateData should contain { status: "NEW_STATUS", collectedKgs: 123.4, notes: "Some notes" }
    return api.put(`/worker/pickups/${pickupId}/status`, updateData);
};

// Fetches all activity logs for the current worker
export const getAllWorkerActivityLogs = () => {
    return api.get('/worker/logs');
};

// You might also have login/registration related to workers here if needed
export const workerLogin = (credentials) => {
    return api.post('/auth/worker/login', credentials);
};

export const workerRegister = (workerData) => {
    return api.post('/auth/worker/register', workerData);
};
