import React, { useState } from 'react';
// Assuming 'workerApi' is a separate service that also uses Axios with its own interceptor for worker tokens
import { loginWorker } from '../services/workerApi';
import { useNavigate } from 'react-router-dom';

const WorkerLogin = () => {
    const [identifier, setIdentifier] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState(''); // Success message
    const [error, setError] = useState(''); // Error message
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage(''); // Clear previous messages
        setError('');   // Clear previous errors

        try {
            // Call the loginWorker function from your workerApi service
            const response = await loginWorker({ identifier, password });

            // On successful login, store worker-specific data in localStorage
            localStorage.setItem('workerToken', response.token); // Store the worker's JWT token
            localStorage.setItem('workerRole', response.role);   // Store the worker's role (should be "worker")
            localStorage.setItem('workerUsername', identifier); // Store username for display if needed

            // Dispatch a custom event to notify other components (like Header) of the login status change
            window.dispatchEvent(new Event('loginStatusChanged'));

            // Navigate to the worker dashboard upon successful login
            navigate('/worker/dashboard');
        } catch (err) {
            // Handle errors from the loginWorker function
            if (typeof err === 'object' && err !== null && err.message) {
                setError(err.message); // Display specific error message if available
            } else if (typeof err === 'string') {
                setError(err); // Display string error
            } else {
                setError('An unknown error occurred during worker login.'); // Generic fallback
            }
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">
            <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-md">
                <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">Worker Login</h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <div>
                        <label htmlFor="identifier" className="block text-sm font-medium text-gray-700">Username:</label>
                        <input
                            type="text"
                            id="identifier"
                            value={identifier}
                            onChange={(e) => setIdentifier(e.target.value)}
                            required
                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                        />
                    </div>
                    <div>
                        <label htmlFor="password" className="block text-sm font-medium text-gray-700">Password:</label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                        />
                    </div>
                    <button
                        type="submit"
                        className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                    >
                        Login
                    </button>
                </form>
                {message && <p className="mt-4 text-center text-green-600">{message}</p>}
                {error && <p className="mt-4 text-center text-red-600">{error}</p>}
            </div>
        </div>
    );
};

export default WorkerLogin;
