import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Header() {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem('isLoggedIn') === 'true');
  // Initialize userRole from localStorage (should now be lowercase)
  const [userRole, setUserRole] = useState(localStorage.getItem('role')); // This will be lowercase

  useEffect(() => {
    const updateLoginStatusAndRole = () => {
      setIsLoggedIn(localStorage.getItem('isLoggedIn') === 'true');
      // Update the userRole state whenever the login status changes
      setUserRole(localStorage.getItem('role')); // This will now get lowercase roles
    };

    // Listen to custom login status event
    window.addEventListener('loginStatusChanged', updateLoginStatusAndRole);

    // Call it once on mount to set initial state
    updateLoginStatusAndRole();

    return () => {
      window.removeEventListener('loginStatusChanged', updateLoginStatusAndRole);
    };
  }, []);

  return (
    <header className="bg-green-800 text-white p-4 shadow-md">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-2xl font-bold cursor-pointer" onClick={() => navigate('/')}>
          ‚ôªÔ∏è E-Waste Management
        </h1>

        <nav className="flex items-center space-x-4">
          <Link to="/" className="hover:text-gray-300">Home</Link>

          {/* Conditional rendering for "Buy" link */}
          {/* Show 'Buy' only if logged in AND role is 'commercial' (lowercase) */}
          {isLoggedIn && userRole === 'commercial' && ( // Check for lowercase 'commercial'
            <Link to="/buy" className="hover:text-gray-300">Buy</Link>
          )}

          <Link to="/maps" className="hover:text-gray-300">Nearest Centers</Link>
          <Link to="/our-mission" className="hover:text-gray-300">Our Mission</Link>

          {!isLoggedIn && (
            <Link to="/login" className="hover:text-gray-300">Login</Link>
          )}

          <Link to="/register" className="hover:text-gray-300">Join us</Link>
          <Link to="/contact" className="hover:text-gray-300">Contact</Link>

          {isLoggedIn && (
            <div
              onClick={() => {
                // Smart navigation for the profile icon based on the actual logged-in role
                if (userRole === 'worker') { // If it's a worker, go to worker dashboard
                  navigate('/worker/dashboard');
                } else if (userRole === 'commercial') { // If it's a commercial user
                    navigate('/commercial-dashboard');
                } else if (['individual', 'charity'].includes(userRole)) { // If it's an individual or charity user
                  navigate('/profile'); // Go to general profile
                } else {
                  navigate('/'); // Fallback for any other unexpected role
                }
              }}
              title="Profile/Dashboard"
              className="ml-4 w-10 h-10 bg-white text-green-800 rounded-full flex items-center justify-center cursor-pointer hover:scale-105 transition-transform"
            >
              üë§
            </div>
          )}
        </nav>
      </div>
    </header>
  );
}

export default Header;
