import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios'; // Make sure you have axios installed: npm install axios
import { useNavigate } from 'react-router-dom';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

// Function to load the Razorpay script dynamically to ensure it's available
const loadRazorpayScript = () => {
    return new Promise((resolve) => {
        const script = document.createElement('script');
        script.src = 'https://checkout.razorpay.com/v1/checkout.js';
        script.onload = () => {
            resolve(true);
        };
        script.onerror = () => {
            resolve(false);
        };
        document.body.appendChild(script);
    });
};

function Pickup() {
    const navigate = useNavigate();
    const fileInputRef = useRef(null); // Reference for file input

    // State for pickup request form data
    const [formData, setFormData] = useState({
        userId: '',
        date: null, // Initialized to null, will be a Date object from DatePicker
        time: '',
        address: '',
        pincode: '',
        city: '',
        state: '',
        schedulerName: '',
        phone: '',
        email: '',
        image: null, // For storing the File object
        wasteType: '',
    });

    const [imagePreview, setImagePreview] = useState(null); // For displaying image preview
    const [loading, setLoading] = useState(false); // For showing loading state
    const [pincodeError, setPincodeError] = useState(''); // For pincode validation messages
    const [dailyPickupCounts, setDailyPickupCounts] = useState({}); // To store pickup counts per day
    const MAX_PICKUPS_PER_DAY = 5; // Define your daily pickup limit

    // IMPORTANT: Set your fixed payment price here in Rupees.
    // This value will be sent to the backend. The backend will convert it to paisa.
    const FIXED_PICKUP_PRICE_RUPEES = 40;

    // Razorpay Public Key ID (starts with rzp_test_...). Get this from your Razorpay Dashboard.
    const RAZORPAY_KEY_ID = 'rzp_test_YOUR_FRONTEND_RAZORPAY_KEY_ID'; // <--- REPLACE THIS WITH YOUR ACTUAL PUBLIC KEY

    // Effect to check login status and fetch user data on mount
    useEffect(() => {
        const isLoggedIn = localStorage.getItem('isLoggedIn');
        const storedUserId = localStorage.getItem('userId');
        if (!isLoggedIn || !storedUserId) {
            navigate('/login');
            return;
        }

        const fetchUserData = async () => {
            try {
                // Assuming you have an API endpoint to get current user's profile based on userId
                const response = await axios.get(`http://localhost:8082/api/user/${storedUserId}`);
                const userData = response.data;
                setFormData(prevDetails => ({
                    ...prevDetails,
                    userId: userData.id,
                    address: userData.location || '', // Assuming 'location' field stores address
                    phone: userData.phone || '',
                    email: userData.email || '',
                    schedulerName: userData.fullname || '', // Assuming 'fullname' is the user's name
                }));
            } catch (error) {
                console.error('Failed to fetch user data:', error);
                // Optionally, navigate to login or show an error
            }
        };

        setFormData(prev => ({ ...prev, userId: storedUserId })); // Set userId from localStorage first
        fetchUserData();
    }, [navigate]);

    // Effect to fetch daily pickup counts from backend
    useEffect(() => {
        const fetchPickupCounts = async () => {
            const today = new Date();
            const startDate = today.toISOString().split('T')[0];
            const futureDate = new Date(today.getFullYear(), today.getMonth() + 2, 0);
            const endDate = futureDate.toISOString().split('T')[0];

            try {
                const response = await axios.get(`http://localhost:8082/api/pickups/counts?startDate=${startDate}&endDate=${endDate}`);
                setDailyPickupCounts(response.data);
            } catch (error) {
                console.error('Error fetching daily pickup counts:', error);
            }
        };
        fetchPickupCounts();
    }, []);

    // Effect to watch for pincode changes and fetch city/state
    useEffect(() => {
        const fetchLocation = async () => {
            const { pincode } = formData;
            if (pincode.length === 6) {
                setPincodeError('');
                try {
                    const response = await fetch(`https://api.postalpincode.in/pincode/${pincode}`);
                    const data = await response.json();

                    if (data && data[0] && data[0].Status === 'Success') {
                        const postOffice = data[0].PostOffice[0];
                        setFormData((prev) => ({
                            ...prev,
                            city: postOffice.District,
                            state: postOffice.State,
                        }));
                    } else {
                        setPincodeError('Invalid Pincode. Please enter a valid 6-digit Pincode.');
                        setFormData((prev) => ({
                            ...prev,
                            city: '',
                            state: '',
                        }));
                    }
                } catch (error) {
                    console.error('Error fetching pincode data:', error);
                    setPincodeError('Failed to fetch location. Please enter manually.');
                    setFormData((prev) => ({
                        ...prev,
                        city: '',
                        state: '',
                    }));
                }
            } else if (pincode.length > 0 && pincode.length < 6) {
                setPincodeError('Pincode must be 6 digits.');
                setFormData((prev) => ({
                    ...prev,
                    city: '',
                    state: '',
                }));
            } else {
                setPincodeError('');
                setFormData((prev) => ({
                    ...prev,
                    city: '',
                    state: '',
                }));
            }
        };

        const debounceTimeout = setTimeout(() => {
            fetchLocation();
        }, 500);

        return () => clearTimeout(debounceTimeout);
    }, [formData.pincode]);


    const getTodayDate = () => {
        const today = new Date();
        today.setHours(0, 0, 0, 0);
        return today;
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    const handleDateChange = (date) => {
        setFormData((prev) => ({ ...prev, date: date })); // DatePicker returns a Date object
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setFormData((prev) => ({ ...prev, image: file }));
            setImagePreview(URL.createObjectURL(file));
        } else {
            setFormData((prev) => ({ ...prev, image: null }));
            setImagePreview(null);
        }
    };

    // Function to submit pickup details after successful payment or for charity users
    const submitPickupDetails = async () => {
        setLoading(true);
        const submitFormData = new FormData();
        submitFormData.append('userId', formData.userId);
        submitFormData.append('date', formData.date ? formData.date.toISOString().split('T')[0] : ''); // Format date
        submitFormData.append('time', formData.time);
        submitFormData.append('address', formData.address);
        submitFormData.append('pincode', formData.pincode);
        submitFormData.append('city', formData.city);
        submitFormData.append('state', formData.state);
        submitFormData.append('schedulerName', formData.schedulerName);
        submitFormData.append('phone', formData.phone);
        submitFormData.append('email', formData.email);
        submitFormData.append('wasteType', formData.wasteType);
        submitFormData.append('status', localStorage.getItem('userRole') === 'charity' ? 'Pending' : 'Paid - Pending Pickup'); // Set status
        if (formData.image) {
            submitFormData.append('image', formData.image);
        }

        try {
            // Your backend endpoint for scheduling pickup
            const res = await axios.post('http://localhost:8082/api/pickups/schedule', submitFormData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    // Add authorization header if your /api/pickups/schedule endpoint is secured
                    // 'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
            });

            if (res.status === 200) { // Assuming 200 OK for success
                alert('Pickup scheduled successfully!');
                navigate('/profile'); // Navigate to user's profile/dashboard
                // Optimistically update local pickup counts
                const dateString = formData.date.toISOString().split('T')[0];
                setDailyPickupCounts(prev => ({
                    ...prev,
                    [dateString]: (prev[dateString] || 0) + 1
                }));
            } else {
                alert(`Failed to schedule pickup. Server response: ${res.status} - ${res.data}`);
            }
        } catch (err) {
            console.error('Error submitting pickup details:', err);
            alert('Error occurred while scheduling pickup. Please try again.');
        } finally {
            setLoading(false);
        }
    };


    // Function to handle payment initiation for non-charity users
    const handlePayment = async () => {
        // Basic frontend validation before proceeding to payment
        if (!formData.date || !formData.time || !formData.address || !formData.pincode ||
            !formData.city || !formData.state || !formData.schedulerName || !formData.phone ||
            !formData.email || !formData.wasteType || !formData.image) {
            alert("Please fill in all required fields and upload an image before proceeding to payment.");
            return;
        }
        if (pincodeError) {
            alert(pincodeError);
            return;
        }

        // Check daily pickup limit for the selected date
        if (formData.date) {
            const dateString = formData.date.toISOString().split('T')[0];
            const selectedDateCount = dailyPickupCounts[dateString] || 0;
            if (selectedDateCount >= MAX_PICKUPS_PER_DAY) {
                alert(`Sorry, the selected date (${dateString}) has reached its maximum number of pickups (${MAX_PICKUPS_PER_DAY}). Please choose another date.`);
                return;
            }
        }

        setLoading(true);
        const scriptLoaded = await loadRazorpayScript();
        if (!scriptLoaded) {
            alert('Razorpay SDK failed to load. Please check your internet connection.');
            setLoading(false);
            return;
        }

        try {
            // 1. Call backend to create Razorpay Order
            const orderRes = await axios.post('http://localhost:8082/api/razorpay/create-order', {
                amount: FIXED_PICKUP_PRICE_RUPEES, // Send amount in Rupees to backend
            });

            const orderData = JSON.parse(orderRes.data); // Backend returns Razorpay Order JSON string

            // 2. Configure Razorpay checkout options
            const options = {
                key: RAZORPAY_KEY_ID, // Your public Razorpay Key ID
                amount: orderData.amount, // Amount in PAISA from the backend response
                currency: orderData.currency, // Currency from backend response
                name: 'E-Waste Management',
                description: 'Pickup Charge',
                order_id: orderData.id, // Order ID from backend response
                handler: async (response) => {
                    // This function runs on successful payment
                    alert('Payment successful! Payment ID: ' + response.razorpay_payment_id);
                    // After successful payment, submit the pickup details to your backend
                    await submitPickupDetails();
                },
                prefill: {
                    name: formData.schedulerName,
                    email: formData.email,
                    contact: formData.phone,
                },
                theme: {
                    color: '#2f855a'
                },
            };

            const razor = new window.Razorpay(options);
            razor.on('payment.failed', function (response) {
                alert("Payment failed: " + response.error.code + " - " + response.error.description);
                console.error("Payment failed details:", response.error);
                setLoading(false);
            });
            razor.open(); // Open the Razorpay payment modal

        } catch (err) {
            console.error('Payment initiation failed:', err);
            if (err.response) {
                try {
                    const errorJson = JSON.parse(err.response.data);
                    alert('Payment initiation failed: ' + (errorJson.error || 'Unknown error from server.'));
                } catch (parseErr) {
                    alert('Payment initiation failed: ' + (err.response.data || err.message));
                }
            } else if (err.request) {
                alert('Payment initiation failed: No response from backend. Is the backend running?');
            } else {
                alert('Payment initiation failed: ' + err.message);
            }
            setLoading(false);
        }
    };

    // Main form submission handler
    const handleSubmit = (e) => {
        e.preventDefault();

        // Perform basic validation before proceeding
        if (!formData.image) {
            alert("Please upload an image of the item.");
            return;
        }
        if (pincodeError) {
            alert(pincodeError);
            return;
        }

        // Check daily pickup limit on submit as well (redundant with handlePayment, but good for charity path)
        if (formData.date) {
            const dateString = formData.date.toISOString().split('T')[0];
            const selectedDateCount = dailyPickupCounts[dateString] || 0;
            if (selectedDateCount >= MAX_PICKUPS_PER_DAY) {
                alert(`Sorry, the selected date (${dateString}) has reached its maximum number of pickups (${MAX_PICKUPS_PER_DAY}). Please choose another date.`);
                return;
            }
        }

        const userRole = localStorage.getItem('userRole'); // Get user role from local storage
        if (userRole === 'charity') {
            submitPickupDetails(); // Charity users don't pay
        } else {
            handlePayment(); // Regular users proceed to payment
        }
    };

    // Helper functions for DatePicker styling
    const isDateDisabled = (date) => {
        const dateString = date.toISOString().split('T')[0];
        const count = dailyPickupCounts[dateString] || 0;
        return count >= MAX_PICKUPS_PER_DAY;
    };

    const highlightDates = (date) => {
        const dateString = date.toISOString().split('T')[0];
        const count = dailyPickupCounts[dateString] || 0;
        return count >= MAX_PICKUPS_PER_DAY ? 'red-date' : '';
    };


    return (
        <div className="flex bg-gray-50 min-h-screen font-sans">
            {/* Main content */}
            <main className="flex-1 p-10">
                <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow-xl">
                    <button onClick={() => navigate('/profile')} className="mb-6 bg-gray-300 hover:bg-gray-400 text-gray-800 font-semibold py-2 px-4 rounded-lg transition duration-200 ease-in-out">
                         Back to Dashboard
                    </button>

                    <h2 className="text-3xl font-bold mb-8 text-green-700 text-center">Schedule E-Waste Pickup</h2>

                    <form onSubmit={handleSubmit} className="space-y-6">
                        {/* Date and Time */}
                        <div className="flex flex-col sm:flex-row space-y-4 sm:space-y-0 sm:space-x-4">
                            <div className="flex-1">
                                <label htmlFor="date" className="block text-gray-700 text-sm font-medium mb-2">Pickup Date</label>
                                <DatePicker
                                    selected={formData.date}
                                    onChange={handleDateChange}
                                    minDate={getTodayDate()}
                                    dateFormat="yyyy-MM-dd"
                                    placeholderText="Select Date"
                                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-green-500 focus:border-green-500 transition duration-150 ease-in-out"
                                    wrapperClassName="w-full"
                                    required
                                    filterDate={(date) => {
                                        const today = getTodayDate();
                                        return date >= today && !isDateDisabled(date);
                                    }}
                                    dayClassName={highlightDates}
                                />
                            </div>
                            <div className="flex-1">
                                <label htmlFor="time" className="block text-gray-700 text-sm font-medium mb-2">Pickup Time</label>
                                <input
                                    type="time"
                                    id="time"
                                    name="time"
                                    value={formData.time}
                                    onChange={handleChange}
                                    required
                                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-green-500 focus:border-green-500 transition duration-150 ease-in-out"
                                />
                            </div>
                        </div>

                        {/* Full Address */}
                        <div>
                            <label htmlFor="address" className="block text-gray-700 text-sm font-medium mb-2">Full Address</label>
                            <textarea
                                id="address"
                                name="address"
                                value={formData.address}
                                onChange={handleChange}
                                placeholder="House No., Street, Locality"
                                required
                                rows="3"
                                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-green-500 focus:border-green-500 transition duration-150 ease-in-out resize-none"
                            ></textarea>
                        </div>

                        {/* Pincode, City, State */}
                        <div className="flex flex-col sm:flex-row space-y-4 sm:space-y-0 sm:space-x-4">
                            <div className="flex-1">
                                <label htmlFor="pincode" className="block text-gray-700 text-sm font-medium mb-2">Pincode</label>
                                <input
                                    type="text"
                                    id="pincode"
                                    name="pincode"
                                    value={formData.pincode}
                                    onChange={handleChange}
                                    placeholder="Enter 6-digit Pincode"
                                    required
                                    className={`w-full p-3 border rounded-lg focus:ring-green-500 focus:border-green-500 transition duration-150 ease-in-out ${pincodeError ? 'border-red-500' : 'border-gray-300'}`}
                                    maxLength="6"
                                />
                                {pincodeError && <p className="text-red-500 text-xs mt-1">{pincodeError}</p>}
                            </div>
                            <div className="flex-1">
                                <label htmlFor="city" className="block text-gray-700 text-sm font-medium mb-2">City</label>
                                <input
                                    type="text"
                                    id="city"
                                    name="city"
                                    value={formData.city}
                                    onChange={handleChange}
                                    placeholder="City"
                                    required
                                    className="w-full p-3 border border-gray-300 rounded-lg bg-gray-100 cursor-not-allowed"
                                    readOnly // City auto-filled by pincode
                                />
                            </div>
                            <div className="flex-1">
                                <label htmlFor="state" className="block text-gray-700 text-sm font-medium mb-2">State</label>
                                <input
                                    type="text"
                                    id="state"
                                    name="state"
                                    value={formData.state}
                                    onChange={handleChange}
                                    placeholder="State"
                                    required
                                    className="w-full p-3 border border-gray-300 rounded-lg bg-gray-100 cursor-not-allowed"
                                    readOnly // State auto-filled by pincode
                                />
                            </div>
                        </div>

                        {/* Scheduler Name & Phone */}
                        <div className="flex flex-col sm:flex-row space-y-4 sm:space-y-0 sm:space-x-4">
                            <div className="flex-1">
                                <label htmlFor="schedulerName" className="block text-gray-700 text-sm font-medium mb-2">Your Name</label>
                                <input
                                    type="text"
                                    id="schedulerName"
                                    name="schedulerName"
                                    value={formData.schedulerName}
                                    onChange={handleChange}
                                    placeholder="Your Full Name"
                                    required
                                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-green-500 focus:border-green-500 transition duration-150 ease-in-out"
                                />
                            </div>
                            <div className="flex-1">
                                <label htmlFor="phone" className="block text-gray-700 text-sm font-medium mb-2">Phone Number</label>
                                <input
                                    type="tel"
                                    id="phone"
                                    name="phone"
                                    value={formData.phone}
                                    onChange={handleChange}
                                    placeholder="e.g., 9876543210"
                                    required
                                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-green-500 focus:border-green-500 transition duration-150 ease-in-out"
                                />
                            </div>
                        </div>

                        {/* Email */}
                        <div>
                            <label htmlFor="email" className="block text-gray-700 text-sm font-medium mb-2">Email</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                placeholder="you@example.com"
                                required
                                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-green-500 focus:border-green-500 transition duration-150 ease-in-out"
                            />
                        </div>

                        {/* Waste Type Dropdown */}
                        <div>
                            <label htmlFor="wasteType" className="block text-gray-700 text-sm font-medium mb-2">Select E-Waste Type</label>
                            <select
                                id="wasteType"
                                name="wasteType"
                                value={formData.wasteType}
                                onChange={handleChange}
                                required
                                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-green-500 focus:border-green-500 transition duration-150 ease-in-out"
                            >
                                <option value="">Select Waste Type</option>
                                <option value="COMPUTERS">Computers</option>
                                <option value="LAPTOPS">Laptops</option>
                                <option value="MONITORS">Monitors</option>
                                <option value="TELEVISIONS">Televisions</option>
                                <option value="MOBILE_PHONES">Mobile Phones</option>
                                <option value="TABLETS">Tablets</option>
                                <option value="BATTERIES">Batteries</option>
                                <option value="PRINTERS">Printers</option>
                                <option value="FAX_MACHINES">Fax Machines</option>
                                <option value="SCANNERS">Scanners</option>
                                <option value="KEYBOARDS">Keyboards</option>
                                <option value="MICE">Mice</option>
                                <option value="CHARGERS">Chargers</option>
                                <option value="AUDIO_EQUIPMENT">Audio Equipment</option>
                                <option value="CABLES">Cables</option>
                                <option value="CIRCUIT_BOARDS">Circuit Boards</option>
                                <option value="GAME_CONSOLES">Game Consoles</option>
                                <option value="CAMERAS">Cameras</option>
                                <option value="POWER_STRIPS">Power Strips</option>
                                <option value="REFRIGERATORS">Refrigerators</option>
                            </select>
                        </div>

                        {/* Image Upload */}
                        <div>
                            <label htmlFor="imageUpload" className="block text-gray-700 text-sm font-medium mb-2">Upload Image of Item(s)</label>
                            <input
                                type="file"
                                id="imageUpload"
                                name="image"
                                accept="image/*"
                                ref={fileInputRef}
                                onChange={handleImageChange}
                                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-green-500 focus:border-green-500 transition duration-150 ease-in-out file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-green-50 file:text-green-700 hover:file:bg-green-100"
                                required
                            />
                            {imagePreview && (
                                <img src={imagePreview} alt="Preview" className="mt-4 w-48 h-48 object-cover rounded-lg shadow-md" />
                            )}
                        </div>

                        {/* Pickup charge info */}
                        <div className="text-lg text-gray-700 font-semibold mt-4 text-center">
                            {localStorage.getItem('userRole') === 'charity' ? (
                                <span className="text-green-600">No pickup charge for charity organizations.</span>
                            ) : (
                                <span>Pickup Service Fee: <span className="text-green-700">{FIXED_PICKUP_PRICE_RUPEES}</span> (via Razorpay)</span>
                            )}
                        </div>

                        {/* Submit Button */}
                        <div className="mt-6">
                            <button
                                type="submit"
                                disabled={loading}
                                className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-6 rounded-lg transition duration-300 ease-in-out transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed shadow-lg"
                            >
                                {loading ? 'Processing...' : 'Schedule Pickup'}
                            </button>
                        </div>
                    </form>
                </div>
            </main>
        </div>
    );
}

export default Pickup;
