import React, { useEffect, useRef, useState } from 'react';

const MapsLeaflet = () => {
  const mapRef = useRef(null);
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(true);
  const [location, setLocation] = useState(null);

  useEffect(() => {
    const loadMap = () => {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          const { latitude, longitude } = pos.coords;
          setLocation({ lat: latitude, lng: longitude });

          const map = new window.google.maps.Map(mapRef.current, {
            center: { lat: latitude, lng: longitude },
            zoom: 14,
          });

          // Marker for user's location
          new window.google.maps.Marker({
            position: { lat: latitude, lng: longitude },
            map,
            title: 'Your Location',
            icon: 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png',
          });

          const service = new window.google.maps.places.PlacesService(map);
          const request = {
            location: { lat: latitude, lng: longitude },
            radius: 10000,
            type: 'store', // can also try 'point_of_interest'
            keyword: 'e-waste recycling kabadiwala scrap dealer electronics waste',
          };

          service.nearbySearch(request, (results, status) => {
            if (status === window.google.maps.places.PlacesServiceStatus.OK) {
              console.log('Nearby results:', results); // Debug log
              setPlaces(results);

              results.forEach((place) => {
                const marker = new window.google.maps.Marker({
                  position: place.geometry.location,
                  map,
                  title: place.name,
                });

                const infoWindow = new window.google.maps.InfoWindow({
                  content: `<strong>${place.name}</strong><br>${place.vicinity}`,
                });

                marker.addListener('click', () => {
                  infoWindow.open(map, marker);
                });
              });

              setLoading(false);
            } else {
              console.warn('No places found:', status);
              setLoading(false);
            }
          });
        },
        (err) => {
          console.error('Geolocation error:', err);
          setLoading(false);
        }
      );
    };

    if (!window.google || !window.google.maps) {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyAH8Mqz2Jazg0Q0tM_XA27CSgzRqS7v2Kk&libraries=places`;
      script.async = true;
      script.defer = true;
      script.onload = loadMap;
      document.body.appendChild(script);
    } else {
      loadMap();
    }
  }, []);

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      {/* Sidebar */}
      <div style={{
        width: '30%',
        padding: '1rem',
        overflowY: 'auto',
        backgroundColor: '#f9f9f9',
        borderRight: '1px solid #ddd',
      }}>
        <h2>Nearby E-Waste Centers</h2>
        {loading ? (
          <p>Loading nearby centers...</p>
        ) : places.length === 0 ? (
          <p>No nearby centers found.</p>
        ) : (
          places.map((place, idx) => (
            <div key={idx} style={{
              marginBottom: '10px',
              padding: '10px',
              backgroundColor: '#fff',
              borderRadius: '6px',
              boxShadow: '0 0 5px rgba(0,0,0,0.1)',
            }}>
              <h4>{place.name}</h4>
              <p>{place.vicinity}</p>
            </div>
          ))
        )}
      </div>

      {/* Map */}
      <div style={{ width: '70%' }}>
        <div ref={mapRef} style={{ width: '100%', height: '100%' }} />
      </div>
    </div>
  );
};

export default MapsLeaflet;
