// src/pages/WorkerRegister.js

import React, { useState } from 'react';

import { registerWorker } from '../services/workerApi';

import { useNavigate } from 'react-router-dom';



const WorkerRegister = () => {

    const [formData, setFormData] = useState({

        username: '',

        password: '',

        fullname: '',

        phone: '',

        email: '',

        city: '', // Added city

        pincode: '' // Added pincode

    });

    const [message, setMessage] = useState('');

    const [error, setError] = useState('');

    const navigate = useNavigate();



    const handleChange = (e) => {

        setFormData({ ...formData, [e.target.name]: e.target.value });

    };



    const handleSubmit = async (e) => {

        e.preventDefault();

        setMessage('');

        setError('');

        try {

            const response = await registerWorker(formData);

            setMessage(response); // response from registerWorker is already a string

            navigate('/worker/login');

        } catch (err) {

            if (typeof err === 'object' && err !== null && err.message) {

                setError(err.message);

            } else if (typeof err === 'string') {

                setError(err);

            } else {

                setError('An unknown error occurred during registration.');

            }

        }

    };



    return (

        <div className="min-h-screen flex items-center justify-center bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">

            <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-md">

                <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">Worker Registration</h2>

                <form onSubmit={handleSubmit} className="space-y-4">

                    <div>

                        <label htmlFor="username" className="block text-sm font-medium text-gray-700">Username:</label>

                        <input

                            type="text"

                            id="username"

                            name="username"

                            value={formData.username}

                            onChange={handleChange}

                            required

                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"

                        />

                    </div>

                    <div>

                        <label htmlFor="password" className="block text-sm font-medium text-gray-700">Password:</label>

                        <input

                            type="password"

                            id="password"

                            name="password"

                            value={formData.password}

                            onChange={handleChange}

                            required

                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"

                        />

                    </div>

                    <div>

                        <label htmlFor="fullname" className="block text-sm font-medium text-gray-700">Full Name:</label>

                        <input

                            type="text"

                            id="fullname"

                            name="fullname"

                            value={formData.fullname}

                            onChange={handleChange}

                            required

                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"

                        />

                    </div>

                    <div>

                        <label htmlFor="phone" className="block text-sm font-medium text-gray-700">Phone:</label>

                        <input

                            type="tel"

                            id="phone"

                            name="phone"

                            value={formData.phone}

                            onChange={handleChange}

                            required

                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"

                        />

                    </div>

                    <div>

                        <label htmlFor="email" className="block text-sm font-medium text-gray-700">Email:</label>

                        <input

                            type="email"

                            id="email"

                            name="email"

                            value={formData.email}

                            onChange={handleChange}

                            required

                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"

                        />

                    </div>

                    {/* New fields for City and Pincode */}

                    <div>

                        <label htmlFor="city" className="block text-sm font-medium text-gray-700">City:</label>

                        <input

                            type="text"

                            id="city"

                            name="city"

                            value={formData.city}

                            onChange={handleChange}

                            required

                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"

                        />

                    </div>

                    <div>

                        <label htmlFor="pincode" className="block text-sm font-medium text-gray-700">Pincode:</label>

                        <input

                            type="text" // Pincodes can sometimes have non-numeric parts, but typically numeric

                            id="pincode"

                            name="pincode"

                            value={formData.pincode}

                            onChange={handleChange}

                            required

                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"

                        />

                    </div>

                     <div>

