// src/components/ProtectedRoute.js
import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';

const ProtectedRoute = ({ allowedRoles }) => {
    const userToken = localStorage.getItem('token');
    const userRole = localStorage.getItem('role'); // This should now be lowercase (e.g., "individual")

    const workerToken = localStorage.getItem('workerToken');
    const workerRole = localStorage.getItem('workerRole'); // This should be "worker"

    let isAuthenticated = false;
    let actualRole = null;

    // Prioritize worker login check if you have separate tokens
    if (workerToken && workerRole === 'worker') { // Check for lowercase 'worker' role
        isAuthenticated = true;
        actualRole = 'worker';
    } else if (userToken && userRole) {
        isAuthenticated = true;
        actualRole = userRole; // This will be the lowercase role from AuthController
    }

    if (!isAuthenticated) {
        return <Navigate to="/login" replace />;
    }

    if (allowedRoles && allowedRoles.length > 0) {
        // allowedRoles array passed from App.js should contain lowercase strings (e.g., ['individual', 'commercial'])
        if (!actualRole || !allowedRoles.includes(actualRole)) {
            console.warn(`Access denied: User role '${actualRole}' not in allowed roles: ${allowedRoles.join(', ')}`);

            // --- SMART REDIRECTION (Crucial for worker issue) ---
            if (actualRole === 'worker') { // If the user IS a worker, redirect them to THEIR dashboard
                return <Navigate to="/worker/dashboard" replace />;
            }
            // If it's a regular user role but they tried to access a route they don't have permission for,
            // send them to THEIR default dashboard (profile or commercial-dashboard).
            else if (['individual', 'commercial', 'charity'].includes(actualRole)) { // All lowercase roles here
                // Further refine this to send to commercial-dashboard if role is 'commercial'
                if (actualRole === 'commercial') {
                    return <Navigate to="/commercial-dashboard" replace />;
                }
                return <Navigate to="/profile" replace />; // Default for individual/charity
            }
            return <Navigate to="/" replace />; // Fallback for any other unexpected role or scenario
        }
    }

    return <Outlet />;
};

export default ProtectedRoute;
